import React from "react";
import { Box } from "rebass";
import { Link, withRouter } from "react-router-dom";
import { push as Menu } from "react-burger-menu";
import styled from "@emotion/styled";
import { MenuIcon } from "../../_assets/icons/menuIcon";

const MenuItem = styled(Link)`
  background-color: ${(props) =>
    props.currentpage === props.targetpage ? "#4919AA" : "#fff"};
  color: ${(props) =>
    props.currentpage === props.targetpage ? "#fff" : "#4919AA"};
  display: flex;
  text-decoration: none;
  border-top: ${(props) =>
    props.isfirst === "true" ? "1px solid #515159" : "0px"};
  padding-left: 10px;
  outline: none;
  &:hover {
    text-decoration: none;
    cursor: pointer;
  }
`;

const TextBox = styled(Box)`
  padding-left: 10px;
`;

var styles = {
  bmBurgerButton: {
    position: "fixed",
    width: "26px",
    height: "17px",
    left: "30px",
    top: "30px",
    outline: "none",
    ":focus": {
      outline: "none"
    },
    color: "black"
  },
  bmCrossButton: {
    height: "24px",
    width: "24px"
  },
  bmCross: {
    background: "#bdc3c7"
  },
  bmMenuWrap: {
    position: "fixed",
    height: "100%"
  },
  bmMenu: {
    height: "100vh",
    background: "#fff",
    paddingTop: "76px",
    fontSize: "1.15em"
  },
  bmMorphShape: {
    fill: "#373a47"
  },
  bmItem: {
    display: "flex",
    alignItems: "center",
    height: "50px",
    borderBottom: "1px solid #515159"
  },
  bmOverlay: {
    background: "rgba(0, 0, 0, 0.3)"
  }
};

class SideMenu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      width: 0
    };
  }

  handleItemClick = () => {
    this.closeMenu();
  };

  closeMenu = () => {
    /* walkaround for closing menu */
    const element = document.getElementsByClassName("bm-overlay")[0];
    element.click();
  };

  render() {
    const {
      history: {
        location: { pathname }
      }
    } = this.props;

    return (
      <Menu
        customBurgerIcon={<MenuIcon fill="#000" fillOpacity={1} />}
        isOpen={false}
        styles={{
          ...styles
        }}
        width={280}
        pageWrapId="page-container-inner"
        outerContainerId="page-container-outer"
        bodyClassName="side-menu-opened"
      >
        <MenuItem
          to="/"
          targetpage="/"
          currentpage={pathname}
          onClick={() => this.handleItemClick()}
          isfirst="true"
        >
          <i className="fas fa-home" />
          <TextBox>Home</TextBox>
        </MenuItem>
      </Menu>
    );
  }
}

export default withRouter(SideMenu);
