import React from "react";
import { Flex } from "rebass";
import { Link } from "react-router-dom";
import styled from "@emotion/styled";
import theme from "../../_styles/theme";
import { SiusLogoIcon } from "../../_assets/icons/siusLogoIcon";
import StylingUtils from "../../utils/StylingUtils";

const HeaderWrapper = styled(Flex)`
  position: fixed;
  top: 0;
  left: 0;
  z-index: 999;
  height: 76px;
  width: 100vw;
  background-color: ${theme.colors.darkGreen};
  box-shadow: rgba(4, 4, 4, 0.3) 0px 2px 8px 0px;
  ${StylingUtils.animationFadeIn()}
`;

const LogoContainer = styled(Flex)`
  margin-left: 100px;
  align-items: center;
`;

const UserContainer = styled(Flex)`
  align-items: center;
  color: white;
  margin-left: auto;
  margin-right: 20px;
`;

export const HeaderBar = () => {
  return (
    <HeaderWrapper>
      <LogoContainer>
        <Link to="/">
          <SiusLogoIcon />
        </Link>
      </LogoContainer>
      <UserContainer>Login</UserContainer>
    </HeaderWrapper>
  );
};

export default HeaderBar;
