class SideMenu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      width: 0
    };
  }

  handleItemClick = () => {
    this.closeMenu();
  };

  closeMenu = () => {
    /* walkaround for closing menu */
    const element = document.getElementsByClassName("bm-overlay")[0];
    element.click();
  };

  render() {
    const {
      history: {
        location: { pathname }
      }
    } = this.props;

    return (
      <Menu
        customBurgerIcon={<MenuIcon fill="#fff" fillOpacity={1} />}
        isOpen={false}
        // styles={{
        //   ...styles
        // }}
        width={280}
        pageWrapId="page-container-inner"
        outerContainerId="page-container-outer"
        bodyClassName="side-menu-opened"
      >
        <MenuItem
          to="/"
          targetpage="/"
          currentpage={pathname}
          onClick={() => this.handleItemClick()}
          isfirst="true"
        >
          <i className="fas fa-home" />
          <TextBox>Home</TextBox>
        </MenuItem>
      </Menu>
    );
  }
}

export default withRouter(SideMenu);
